import random

def guessComputerNumber():
  num = random.randint(1,101)
  status = True

  print("Bienvenido al juego 'Adivine el número secreto'! ")

  while(status):
    try:
      guess = int(input("Ingrese un número de 1 a 100: "))
      if(guess == num):
        print("Ud ha adivinado el número secreto. Felicitaciones!")
        status = False
      elif (guess < num):
        print(f"El número secreto es mayor que {guess}")
      elif(guess > num):
        print(f"El número secreto es menor que {guess}")
    except ValueError:
      print("Input inválido")


def guessUserNumber():
    low = 0
    high = 100
    mid = 50
    gameWon = False
    print("Piense en un número entre 0 y 100")
    while(not gameWon):
      mid = (low + high)//2
      ans = input(f"Es su número {mid}? SI/NO: ")
      if(ans == "SI"):
        print("JA! Te he ganado humano")
        gameWon = True
      elif(ans=="NO"):
        
        ans = input(f"Es su número mayor que {mid}? SI/NO: ")
        if(ans == "SI"):
          low = mid+1
        elif(ans == "NO"):  
          high = mid-1
      else:
        print("Input inválido!")



