#identificar las maneras de organizar una palabra sobre la otra para un crucigrama
texto_1 = input("primera palabra: ")
texto_2 = input("segunda palabra: ")

posibilidades = int(0)

#revisar las posibilidades
for i in range(len(texto_1)):
  for j in range(len(texto_2)):
    if texto_1[i] == texto_2[j]:
      posibilidades = posibilidades + 1

print(f'el numero de maneras de organizar las palabras es: {posibilidades}')
