import random

intentos = 0
adivinanza = False

print("Bienvenido al juego de adivinar el numero")
print("Selecciona la dificultad")
print("1. Facil")
print("2. Medio")
print("3. Dificil")
dificultad=int(input("Ingresa el numero de dificultad que desees: "))
while dificultad<1 or dificultad>3:
    print("Opcion no valida")
    dificultad=int(input("Ingresa el numero de dificultad que desees: "))
if dificultad==1:
    numero_secreto=random.randint(1,10)
elif dificultad==2:
    numero_secreto=random.randint(1,100)
else:
    numero_secreto = random.randint(1, 1000)
while adivinanza == False:
    numero_ingresado = int(input("Ingrese un numero: "))
    intentos += 1
    if numero_ingresado == numero_secreto:
        adivinanza = True
        print(f"¡Felicidades! Adivinaste el numero en {intentos} intentos.")
    else:
        if numero_ingresado < numero_secreto:
            print("El numero ingresado es menor al numero secreto")
        else:
            print("El numero ingresado es mayor al numero secreto")
