print('Bienvenidos a la calculadora!!')
print('Para salir escriba salir')
print('Las operaciones son suma, multi, div y resta')

resultado = ''
while True:
    if not resultado:
        resultado = input('Ingrese un número: ')
        if resultado.lower() == 'salir':
            print('Cerrando Calculadora')
            break
        resultado = float(resultado)
    op = input('Ingresa la operación: ')
    if op.lower() == 'salir':
        break
    n2 = input('Ingrese el siguiente número: ')
    if n2.lower() == 'salir':
        break
    n2 = float(n2)

    if op.lower() == 'suma':
        resultado += n2
    elif op.lower() == 'resta':
        resultado -= n2
    elif op.lower() == 'multi':
        resultado *= n2
    elif op.lower() == 'div':
        resultado /= n2
    else:
        print('Operación no válida')
        break

    print(f'El resultado es: {resultado}')
